import React from "react";
import employeeManager from "./employeemanager";

type PropsT = {};
type StateT = {};

export default class Employee extends React.Component<PropsT, StateT> {
  constructor(props) {
    super(props);
    this.state = {
      emprecords: [],
    };
    this.filterByDep = this.filterByDep.bind(this);
  }
  filterByDep() {
      console.log()
  }
  componentDidMount() {
    employeeManager.getEmployeesDataFromService().then((data) => {
      console.log("data", data);
      this.setState({ emprecords: data });
    });
  }

  render() {
    let emprows = this.state.emprecords.map((row, index) => {
      return (
        <div key={index}>
          <span style={{ margin: "15px" }}>{row.empid}</span>
          <span style={{ margin: "15px" }}>{row.name}</span>
          <span style={{ margin: "15px" }}>{row.email}</span>
          <span style={{ margin: "15px" }}>{row.mobile}</span>
          <span style={{ margin: "15px" }}>{row.department}</span>
          <span style={{ margin: "15px" }}>{row.location}</span>
        </div>
      );
    });
    return (
      <div style={{ marginBottom: "20px" }}>
        <div style={{ marginBottom: "20px" }}>
          <input type="textbox" placeholder="Filter By Department" id="depFilter" onChange="this.filterByDep()"></input>
        </div>

        <div>{emprows}</div>
      </div>
    );
  }
}

